import axios from 'axios';

const apiUrl = "http://localhost:5052"

export default {
  getTasks: async () => {
    console.log("shalom");
    const result = await axios.get(`${apiUrl}/items`) ;
    console.log("uvracha",result);  
    if(result.data!=[]) 
    return result.data;
  else
  return []
  },

  addTask: async(name)=>{
    const result = await axios.post(`${apiUrl}/items`, name)    
    return {};
  },

  setCompleted: async(id, isComplete)=>{
    let tasks=[];
    axios.get(`${apiUrl}/items`).then(x=>{tasks=x.data
    let t=tasks.filter(x=>x.id==id)[0];
    t.isComplete=isComplete;
    axios.put(`${apiUrl}/items/${t.id}`, t)})
  },

  deleteTask:async(id)=>{
    await axios.delete(`${apiUrl}/items/${id}`)
  }
};
