import axios from 'axios';

const apiUrl = "https://localhost:5052"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
    //TODO
    const result = await axios.post(`${apiUrl}/items`, name)    
    return {};
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    let tasks=[];
    axios.get(`${apiUrl}/items`).then(x=>tasks=x.data);
    let t=tasks.filter(x=>x.Id==id)[0];
    t.IsCompleted=isComplete;
    await axios.put(`${apiUrl}/items`, t)
    return {};
  },

